'use strict'

/**
 * Module Dependencies
 *
 * @ignore
 */
import Dispatcher from '../router/dispatcher'
import mixin      from '../mixin'
import Router     from '../router/index'
import utils      from '../utils'

/**
 * Application Class
 *
 * @public
 * @class Application
 */
class Application {

  /**
   * Application constructor
   *
   * @public
   * @constructor
   * @param {Object} options
   */
  constructor(options = {}) {
    /**
     * Application UID
     *
     * @private
     * @type {String}
     */
    this.uid = utils.uniqueId(this)

    /**
     * Disposed
     *
     * @private
     * @type {Object}
     */
    this.disposed = false

    /**
     * Options
     *
     * @private
     * @type {Object}
     */
    this.options = options

    if(!this.options.router.routes) return console.error('Application requires routes', this.options)

    /**
     * Router.
     *
     * @private
     * @type {Object}
     */
    this.router = new Router({
      routes: this.options.router.routes
    })

    /**
     * Dispatcher.
     *
     * @private
     * @type {Object}
     */
    var dispatcher = this.dispatcher = new Dispatcher({
      app: this,
      getController: options.dispatcher.getController
    })

    this.router.on('match', function routeMatchCallback(route, params, options) {

      dispatcher.dispatch(route, params, options)

    })

  }

  /**
   * Dispose of the application
   *
   * @example
   *   application.dispose()
   *
   * @public
   */
  dispose() {

    if(!this.disposed) {

      utils.dispose(this)

    }

  }

  /**
   * Start the application
   *
   * @example
   *   application.start()
   *
   * @public
   */
  start() {

    this.router.start()

  }

}

Object.assign(Application.prototype, {mixin})

export default Application

'use strict'

/**
 * Module Dependencies
 *
 * @ignore
 */
import EventEmitter from '../events'
import mixin        from '../mixin'
import Model        from '../models/index'
import utils        from '../utils'

/**
 * Collection Class
 *
 * @public
 * @class Collection
 */
class Collection {

  /**
   * Collection constructor
   *
   * @public
   * @constructor
   * @param {Array} entries
   * @param {Object} options
   */
  constructor(entries = [], options = {}) {
    this.uid = utils.uniqueId(this)

    // TODO: figure out how to mixin this
    var eventEmitter = new EventEmitter()

    this.emit  = eventEmitter.emitEvent.bind(eventEmitter)
    this.on    = eventEmitter.addListener.bind(eventEmitter)
    this.once  = eventEmitter.addOnceListener.bind(eventEmitter)
    this.off   = eventEmitter.removeListener.bind(eventEmitter)

    this.disposed = false
    this.Model    = options.Model || Model
    this.models   = []
    this.options  = options
    this.url      = options.url || this.url || ''

    if(!this.Model || !(this.Model instanceof Function)) {
      throw new Error('Collection requires a valid Model Class')
    }

    if(typeof this.url != 'string') {
      throw new Error('Collection requires a valid url')
    }

    this.ensureEntries(entries)
  }

  add(entries, options = {}) {
    this.ensureEntries(entries, options)
  }

  clear() {
    this.models = []
  }

  /*
  TODO: Clean this up, make checks stronger
  Currently, a mixed array of objects and models could be passed; it's an odd scenario that would cause that to hapen thoug.
  Tecnnically a model instance of a different model could be passed; need to check and prevent that.
  Should also consider concatting arrays as pushing arrays of 1000 or more can be very time consuming/lots of looping.
  Overall this function sucks but helps move the project forward atm.
  */

  ensureEntries(entries, options = {}) {

    /*
    TODO: figure out how to clean this up
    */
    if(
      typeof entries == 'null' ||
      typeof entries == 'undefined' ||
      (!entries.length && typeof entries == 'object' && Object.keys(entries).length == 0)
    ) return

    // we will suppport all kind of iterable  here !!
    // It is simpler to manage things by making a single item an array
    if(!(entries[Symbol.iterator])) {
      entries = [entries]
    }

    if(options.at) options.at--
    for(let entry of entries) {
      var model = null

      /*
      TODO: should create a collection that supports multiple types of  models
      */
      if(entry instanceof this.Model) {
        model = entry
        this.models.push(model)
      }

      else {

        /*
        TODO:
        somehow the first item in this.models gets duplicated to the end, and then new items that were added are added :/
        */
        // If collection has entry, merge the results
        if(entry.id) {

          var index = null
          var existingModel = this.models.filter( (item, i) => {
            if(item.attr.id == entry.id) {
              index = i
              return item
            }
          })

          if(existingModel.length > 0 && index) {
            model = Object.assign(this.models[index], entry)
          }
        }

        // Create a new entry
        if(!model) {
          model = new this.Model(entry, {storeAutoLoad: false})

          if(options.at) {
            options.at++
            this.models.splice(options.at, 0, entry)
            //this.models.splice.apply(this.models, [optoins.at, 0].concat([model]))
          }

          else {
            this.models.push(model)
          }

        }

      }

      this.emit('change', model, this.models.length - 1)
    }

  }

  /*
  TODO: not really sure what to call this function
  */
  getData() {
    var data = []

    this.models.forEach( (model) => {
      data.push(model.getData())
    })

    return data
  }

  move(fromIndex, toIndex, options = {}) {
    this.models.splice(toIndex, 0, this.models.splice(fromIndex, 1)[0])
  }

  remove(index, options = {}) {
    var changeEvent = (typeof options.changeEvent == 'boolean') ? options.changeEvent : true

    this.models.splice(index, 1)

    if(changeEvent) {
      this.emit('change')
      this.emit('removed')
    }
  }

  toJSON() {
    return this.models.map( (model) => model.toJSON())
  }

  [Symbol.iterator](){
    var collectionsModels = this.models,
        index = 0

    return {
      next: function next () {
        if (index + 1 > collectionsModels.length) {
          return { done: true };
        }
        return { value: collectionsModels[index++], done: false };
      }
    }
  }

  /**
   * Dispose of the application
   *
   * @example
   *   application.dispose()
   *
   * @public
   */
  dispose() {

    if(!this.disposed) {

      utils.dispose(this)

    }

  }

}

Object.assign(Collection.prototype, {mixin})

module.exports = Collection

'use strict'

/**
 * Module Dependencies
 *
 * @ignore
 */
import EventEmitter from '../events'
import mixin        from '../mixin'
import View         from '../views/index'
import utils        from '../utils'

/**
 * Component Class
 *
 * @public
 * @class Component
 */
class Component {

  /**
   * Component constructor
   *
   * @public
   * @constructor
   * @param {Object} options
   */
  constructor(options = {}) {
    this.uid = utils.uniqueId(this)
    this.$ = View.prototype.$

    // TODO: figure out how to mixin this
    var eventEmitter = new EventEmitter()

    this.emit  = eventEmitter.emitEvent.bind(eventEmitter)
    this.on    = eventEmitter.addListener.bind(eventEmitter)
    this.once  = eventEmitter.addOnceListener.bind(eventEmitter)
    this.off   = eventEmitter.removeListener.bind(eventEmitter)

    this.attached = false
    this.attachOnInit = options.attachOnInit || true
    this.disposed = false

    /*
    Direct Options
    Some options are important enough that they should be directly on the view. Also offers consistency for overriding certain properties.
    */
    this.directOptions = [
      'attachOnInit',
      'attachPlacement',
      'class', // why did CSS use this?
      'collection',
      'container',
      'id',
      'model',
      'renderOnInit',
      'tagName'
    ]

    View.prototype.assignOptions.call(this, options)

    //this._registry = {}

    //console.log(this)
    View.prototype.ensureContainer.call(this)

    if(!this.attached && this.attachOnInit) {

      this.once('render', () => {

        View.prototype.attach.call(this)

      })

    }

    this.render()
  }

  /*get() {

  }

  set(name, ) {

  }*/

  render() {
    this.el = document.createElement(this.tagName || 'div')

    if(this.id)    this.el.id        = this.id
    if(this.class) this.el.className = this.class

    this.emit('render')

    return this
  }

  dispose() {

    if(!this.disposed) {

      utils.dispose(this)

    }

  }

}

Object.assign(Component.prototype, {
  mixin
})

export default Component

'use strict'

/**
 * Module Dependencies
 *
 * @ignore
 */
import composeStore from '../lib/composeStore'
import utils        from '../utils'

/**
 * Controller Class
 *
 * @public
 * @class Controller
 */
class Controller {

  /**
   * Controller constructor
   *
   * @public
   * @constructor
   * @param {Object} options
   */
  constructor(options = {}) {
    this.uid = utils.uniqueId(this)
  }

  compose(key, Val, options = {}) {

    if(arguments.length == 1 || typeof composeStore[key] == 'object') {
      var composition = composeStore[key]

      return composition
    }

    /*
    TODO: Figure out how updating/replacing a composition would work.
    */
    if(!composeStore[key] || options.update) {

      composeStore[key] = new Val(options)

    }

  }

  dispose() {

    if(!this.disposed) {

      utils.dispose(this, {
        ignoreProperties: ['app']
      })

    }

  }

}

Controller.prototype.disposed = false

export default Controller

